import React from 'react'
import congrats from './congrats.gif'

const style = {
	width: "300px",
	display: "grid",
    fontSize: "40px",
	fontWeight: "800",
	textAlign:'center',
	color: 'white'
};


const Message = (props) => (
	<div style={{ display: 'flex', alignItems : 'center' }}>
		{/*  */}

		{props.value.includes('WON') ? (
			<div>
				<h1 name={"msg"} style={style}>WON: </h1>
				<img src={congrats} style={{ position: 'absolute', width: 100, height: 100 }} />
				<h1 name={"msg"} style={style}>{props.value}</h1>
			</div>
		) : (
			<h1 name={"msg"} style={style}>{props.value}</h1>
		)}
	</div>
)

export default Message
