import React from 'react'
import zoom from './zoom.png'
import plexco from './plexco.png'

const style = {
	border: "3px solid black",
	fontSize: "40px",
}   


/*
Box.jsx is the discrete unit structure of the board ui.
It is made a button.
When its pressed it will call the function passed from porps.
*/

const renderBox = (props) => {
	if (props.value === 'O') {
		return <img name={props.name} onClick={props.onClick} src={plexco} style={{ width: 160, height: 160, border: '12px solid black', backgroundColor: 'white' }} />
	} else if (props.value === 'X') {
		return <img name={props.name} onClick={props.onClick} src={zoom} style={{ width: 160, height: 160, border: '12px solid black', backgroundColor: 'white' }} />
	} else {
		return <div name={props.name} onClick={props.onClick} style={{ width: 160, height: 160, border: '12px solid black', backgroundColor: 'white' }} />
	}
}

const Box = (props) => renderBox(props)

export default Box
