import React from 'react'
import william from './william.png'
import dagul from './dagul.png'

const style = {
	border: "3px solid black",
	fontSize: "40px",
}   


/*
Box.jsx is the discrete unit structure of the board ui.
It is made a button.
When its pressed it will call the function passed from porps.
*/
const Box = (props) => (
	props.value === 'X' ? (
		<img name={props.name} onClick={props.onClick} src={william} width="100px" height="100px" />
	) : (
		<div style={{ width: 100, height: 100, border: '1px solid black' }} />
	)
)

export default Box
